import Modal from "react-bootstrap/Modal";
import Link from "next/link";

const Prompt = (props) => {
  console.log("INSIDE CONFIRMATION CODE: ", props);
  return (
    <Modal show={props.show} onHide={props.handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Please note.</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        A confirmation has been sent to your email. Please retrieve the code and
        confirm acount
      </Modal.Body>
      <Modal.Footer>
        <Link href="/marketplace/confirmaccount">
          <a>Confirm</a>
        </Link>
      </Modal.Footer>
    </Modal>
  );
};

export default Prompt;
