import Link from "next/link";
import { useState } from "react";

const Prompt = (props: any) => {
  const [closeBox, setCloseBox] = useState(props.show);
  return (
    <div
      id="myModal"
      className="modal overlay"
      style={{ display: closeBox ? "block" : "", zIndex: 9999 }}
    >
      {/* <!-- Modal content --> */}
      <div
        className="modal-content"
        style={{
          width: "400px",
          position: "absolute",
          left: "50%",
          top: "50%",
          marginLeft: "-150px",
          marginTop: "-150px",
          padding: "20px",
          textAlign: "center",
        }}
      >
        {/* <p>{props.title}</p> */}
        <h5> {props.children}</h5>
        <div
          style={{ display: "flex", textAlign: "center", marginLeft: "35%" }}
        >
          {props.success ? (
            <Link href={props.linkTo}>
              <a className="btn btn-primary">{props.linkText}</a>
            </Link>
          ) : (
            <a href="#">{props.linkText}</a>
          )}
        </div>
      </div>
    </div>
  );
};

export default Prompt;
