import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Prompt from "../../components/Prompt";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import axios from "axios";

// Font Awesome
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEye } from "@fortawesome/free-solid-svg-icons";
const eye = <FontAwesomeIcon icon={faEye} />;

import styles from "./styles.module.css";

// Utils
import emailPhoneValidation from "../../utils/emailPhoneValidation";

// Hooks
import React, { useState } from "react";

// Next stuff
import Link from "next/link";

const Signup = () => {
  const [validated, setValidated] = useState(false);
  const [emailPhone, setEmailPhone] = useState("");
  const [emailPhoneValidity, setEmailPhoneValidity] = useState("invalid");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("mismatch");
  const [passwordFieldValidity, setPasswordFieldValidity] = useState("invalid");
  const [userType, setUserType] = useState("Organization");
  const [isSent, setIsSent] = useState(false);
  const [passwordShown, setPasswordShown] = useState(false);
  const [show, setShow] = useState(false);

  
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  // Show Modal
  // const showModal = () => {
  //   console.log("inside showModal()");
  //   setShow(true);
  //   return <ConfirmationCode show={show} onHide={handleClose} />;
  // };

  // Toggle Password Visibility
  const togglePasswordVisiblity = () => {
    setPasswordShown(passwordShown ? false : true);
  };

  // Email Phone Validation
  const validatePhoneEmail = (e) => {
    if (emailPhoneValidation(e.target.value)) {
      setEmailPhone(e.target.value);
      setEmailPhoneValidity("valid");
    } else {
      setEmailPhoneValidity("invalid");
    }
  };

  // Password Validation
  const validatePassword = (e) => {
    if (
      e.target.value.match(/[a-z]/g) &&
      e.target.value.match(/[A-Z]/g) &&
      e.target.value.match(/[0-9]/g) &&
      e.target.value.match(/[^a-zA-Z\d]/g) &&
      e.target.value.length >= 8
    ) {
      // res = "TRUE";
      setPassword(e.target.value);
      setPasswordFieldValidity("valid");
    }
    // return true
    // set field to invalid
    else {
      // console.log("invalid password")
      setPasswordFieldValidity("invalid");
    }
  };

  const confirmPasswordMatch = (e) => {
    if (e.target.value === password) {
      setConfirmPassword("match");
    } else {
      setConfirmPassword("mismatch");
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    } else if (confirmPassword === "match") {
      setValidated(true);
      let data = {
        emailPhone,
        password,
        userType,
      };
      console.log("DATA TO BE SENT:", data);
      handleShow();
    }
  };

  return (
    <div>
      <Prompt
        // body={`A confirmation has been sent to your email. Please retrieve the code and
        // confirm acount`}
        linkTo={`confirmaccount`}
        linkText={`Confirm Account`}
        show={show}
        handleClose={handleClose}
      >
        <p>
          A confirmation has been sent to your email. Please retrieve the code
          and confirm acount
        </p>
      </Prompt>

      <CardComponent>
        <HeaderComponent />
      </CardComponent>
      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h4>
                Market <span>Circle</span>
              </h4>
              <h5>Make the most of your business</h5>
              <h6>
                Already on market circle?
                <Link href="/auth/login">
                  <a>Login</a>
                </Link>
              </h6>
            </header>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="text"
                  id="signup_email"
                  className="form-control"
                  placeholder="Please enter a valid email.."
                  required
                  defaultValue={emailPhone}
                  onChange={validatePhoneEmail}
                />
                <small id="emailHelp" className="form-text text-muted">
                  {emailPhoneValidity}
                </small>
                <div className="invalid-feedback">
                  Please enter valid email.
                </div>
              </div>
              <div className={styles.passWrapper}>
                <div className="form-group">
                  <label>Password</label>
                  <input
                    className="form-control"
                    id="signup_password"
                    type={passwordShown ? "text" : "password"}
                    placeholder="Password must be atleast 8 characters long."
                    required
                    defaultValue={password}
                    onChange={validatePassword}
                  />
                  <i onClick={togglePasswordVisiblity}>{eye}</i>
                  <small id="emailHelp" className="form-text text-muted">
                    {passwordFieldValidity}
                  </small>
                  {/* <Form.Control.Feedback type="invalid">
                  Please enter a valid password.
                </Form.Control.Feedback> */}
                </div>
              </div>
              <div className="form-group">
                <label>Confirm Password</label>
                <input
                  id="formBasicPassword"
                  className="form-control"
                  type="signup_confirm_password"
                  placeholder="Password must be atleast 8 characters long."
                  required
                  // defaultValue={confirmPassword}
                  onChange={confirmPasswordMatch}
                />
                <small id="emailHelp" className="form-text text-muted">
                  {confirmPassword}
                </small>
              </div>

              <div className="mb-3 text-center">
                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    id="inline-radio-1"
                    name="radioButton"
                    checked={userType === "Organization"}
                    value="Organization"
                    onChange={(e) => setUserType(e.target.value)}
                  />
                  <label className="form-check-label" htmlFor="inline-radio-1">
                    Organization
                  </label>
                </div>
                {/* custom-control custom-radio custom-control-inline */}
                <div className="form-check form-check-inline">
                  <input
                    className="form-check-input"
                    type="radio"
                    id="inline-radio-2"
                    name="radioButton"
                    // checked={userType === "Individual"}
                    value="Individual"
                    onChange={(e) => setUserType(e.target.value)}
                  />
                  <label className="form-check-label" htmlFor="inline-radio-2">
                    Individual
                  </label>
                </div>
              </div>
              <button
                id="signup_button"
                className="btn btn-primary btn-lg btn-block"
                type="submit"
              >
                Signup
              </button>
            </Form>
            {/* Footer Section */}
            <footer className="text-center">
              <h6>By clicking sign up, you agree to the Market Circle</h6>
              <h6>
                <strong>
                  User Agreement, Privacy Policy and Cookie Policy
                </strong>
              </h6>
            </footer>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default Signup;
