import React,{useState} from "react";
import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Prompt from "../../components/Prompt";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

import HeadComponent from "../../components/HeadComponent";

// Next stuff
import Link from "next/link";

// Utils
import emailPhoneValidation from "../../utils/emailPhoneValidation";

const ForgottenPassword = () => {
  const [show, setShow] = useState(false);
  const [isInputValid, setIsInputValid] = useState(null);
  const [promptBody, setPromptBody] = useState(null);
  const [isRegistered, setIsRegistered] = useState(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = (event) => {
    event.preventDefault();

    // Email Phone Validation
    const validatePhoneEmail = (e) => {
      if (emailPhoneValidation(e.target.value)) {
        // setIsInputValid("valid");
        setPromptBody("Please enter a valid email")

        if(isRegistered === false){
          setPromptBody("No account exists for this email address. Would you like to register?")
        }else{

        }
      } else {
        // setIsInputValid("invalid");
        setPromptBody("Please enter a valid email")
      }
    };    
    handleShow()
  }

  return (
    <div>
      {/* Conditional Render */}
      <Prompt show={show} handleClose={handleClose}>
        <p>
          the password reset code has been sent to your phone or email.
        </p>
      </Prompt>
      <HeadComponent pageTitle="Forgotten Password" />
      <CardComponent>
        <HeaderComponent />
      </CardComponent>

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h1>Forgotten Password</h1>
            </header>
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email/Phone Number</label>
                <input
                  type="email"
                  className="form-control"
                  id="formBasicEmail"
                  placeholder="Please enter a valid email or phone number."
                />
                <small id="emailHelp" className="form-text text-muted"></small>
              </div>

              <button className="btn btn-primary btn-lg btn-block" type="submit">
                Continue
              </button>
            </Form>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default ForgottenPassword;
