import React, { useState } from "react";
import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Prompt from "../../components/Prompt";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

// Next stuff
import Link from "next/link";

const Login = () => {
  const [show, setShow] = useState(false);
  const [isRegistered, setIsRegistered] = useState(null);
  const [isInputValid, setIsInputValid] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(null);
  const [promptBody, setPromptBody] = useState("");
  const [linkTo, setLinkTo] = useState(null);
  const [linkText, setLinkText] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const loader = () => (
    <span
      className="spinner-border spinner-border-sm"
      role="status"
      aria-hidden="true"
      style={{ width: "1.5rem", height: "1.5rem" }}
    ></span>
  );

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    try {
      const result = await axios.post("http://51.116.114.155:8080/auth/token", {
        authentication_property: "james@frontend.com",
        password: "passw0rd1",
        // authentication_property: "ogembodennis@gmail.com",
        // password: "@Beloved2020",
      });

      if (result) {
        setLoading(false);
        console.log("RESULT:", result);
        if (result.status === 200) {
          // go to landing page
          setPromptBody("Replace this prompt with landing page");
          setShow(true);
          setLoading(false);
        }
      }
    } catch (err) {
      console.log(err.message);
      if (err.message === "Request failed with status code 401") {
        // bad credentials
        console.log("BAD CREDENTIALS");
        setPromptBody("Please check your credentials and try again.");
        setLinkTo("signup");
        setLinkText("Signup");
        setShow(true);
        setLoading(false);
      } else if (err.message === "Request failed with status code 404") {
        // bad endpoint
      } else if (err.message === "Network Error") {
        // bad network connection
        setPromptBody("Please check your network connection and try again.");
        setShow(true);
        setLoading(false);
      }
    }
  };

  return (
    <div>
      <Prompt
        linkTo={linkTo}
        linkText={linkText}
        // body={promptBody}
        show={show}
        handleClose={handleClose}
      >
        <p>{promptBody}</p>
      </Prompt>

      <HeaderComponent />

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h1>Login</h1>
            </header>
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  className="form-control"
                  id="login_email"
                  placeholder="Please enter a valid email"
                  onChange={(e) => setEmail(e.target.value)}
                />

                <small id="emailHelp" className="form-text text-muted">
                  {/* {emailPhoneValidity} */}
                </small>
              </div>

              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="login_password"
                  placeholder="Password must be atleast 8 characters long."
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <button
                id="login_button"
                className="btn btn-primary btn-lg btn-block"
                type="submit"
              >
                {loading ? loader() : null}
                Login
              </button>
            </Form>
            {/* Footer Section */}
            <footer className="text-center">
              <h6>
                <Link href="/auth/forgottenpassword">
                  <a>Forgoten Password?</a>
                </Link>
              </h6>
              <h6>
                Don't have an account?{" "}
                <Link href="/auth/signup">
                  <a>Signup</a>
                </Link>
              </h6>
            </footer>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default Login;
