import Button from "react-bootstrap/Button";
import Link from "next/link";
import { useState } from "react";
import axios from "axios";
import { useRouter } from "next/router";
import Prompt from "../../components/Prompt";
import AuthHeader from "../../components/auth/AuthHeader";

const ConfirmAccount = () => {
  const [code1, setCode1] = useState("");
  const [code2, setCode2] = useState("");
  const [code3, setCode3] = useState("");
  const [code4, setCode4] = useState("");
  const [show, setShow] = useState(false);
  const [prompt_title, setPromptTitle] = useState("");
  const [prompt_body, setPromptBody] = useState("");
  const [link_to, setLinkTo] = useState("");
  const [link_text, setLinkText] = useState("");

  const router = useRouter();

  const handleClose = () => setShow(false);

  const callPrompt = (
    title: string,
    link: string,
    link_text: string,
    message: string
  ) => {
    setShow(true);
    setPromptTitle(title);
    setLinkText(link_text);
    setLinkTo(link);
    setPromptBody(message);
  };

  const submitCode = async () => {
    try {
      const rs: any = await axios.post(
        "http://51.116.114.155:8080/auth/keyinput/",
        {
          integer_key: parseInt(`${code1}${code2}${code3}${code4}`),
        }
      );
      console.log(rs);
      setPromptBody("Verification successful");
      setLinkTo("login");
      setLinkText("Login");
      setShow(true);
      callPrompt(
        "Verification",
        "/auth/login",
        "Login",
        "Verification successful"
      );
    } catch (e) {
      console.log(e);
      setPromptBody("Verification failed");
      setLinkTo("login");
      setLinkText("");
      setShow(true);
      callPrompt(
        "Verification",
        "/auth/confirmaccount",
        "Verification Account",
        "Verification failed"
      );
    }
  };

  return (
    <AuthHeader title="Verify Code">
      <style jsx>{`
        .container {
          margin: 0 auto;
          width: 30%;
          background: #ffffff;
          padding: 25px;
        }
        .submitbutton {
          background: #3964fc;
          border: #3964fc;
          border-radius: 10px;
        }
        :hover .submitbutton {
          background: #3964fc;
          border: #3964fc;
        }
        .cinput {
          border-radius: 10px;
        }
        .input-group-addon:last-child {
          background-color: #ffffff;
          border-radius: 0px 10px 10px 0px;
        }
      `}</style>
      <div className="row">
        <div className="navbar">
          <img className="logo" src="/images/Logo.png" />
        </div>
      </div>
      <h4>Confirm</h4>
    </AuthHeader>
  );
};

export default ConfirmAccount;
