  return (
    <div>
      {/* Conditional Render */}
      <Prompt
        show={show}
        handleClose={handleClose}
        linkTo={linkTo}
        linkText={linkText}
      >
        <p>{promptBody}</p>
      </Prompt>
      <HeadComponent pageTitle="Forgotten Password" />
      <CardComponent>
        <HeaderComponent />
      </CardComponent>

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h1>Forgotten Password</h1>
            </header>
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email/Phone Number</label>
                <input
                  type="email"
                  className="form-control"
                  id="formBasicEmail"
                  onChange={validatePhoneEmail}
                  placeholder="Please enter a valid email or phone number."
                />
                <small id="emailHelp" className="form-text text-muted"></small>
              </div>

              <button
                className="btn btn-primary btn-lg btn-block"
                type="submit"
              >
                Continue
              </button>
            </Form>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default ForgottenPassword;
