import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
// import "../../public/style.css";
import React, { useState } from "react";

const ReactCodeInput = dynamic(import("react-code-input"));

// Next stuff
import Link from "next/link";
import dynamic from "next/dynamic";

const ConfirmAaccount = () => {
  const [code, setCode] = useState("");
  return (
    <div>
      <HeaderComponent />
      <div className="wrapper">
        <CenterContent>
          <ContainerComponent>
            <CardComponent width="27em">
              <header className="text-center">
                <h2>Enter code the verfication </h2>
              </header>
              <div className="text-center">
                <ReactCodeInput
                  type="text"
                  fields={4}
                  onChange={(e)=>setCode}
                  inputMode="numeric"
                  name="code"
                />
              </div>
              {/* <form>
                <div>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                </div>
              </form> */}

              <Button
                variant="primary"
                type="submit"
                block
                size="lg"
                className="signup-btn"
              >
                Continue
              </Button>
              <br />
              <div className="text-center">
                If you don't receive the code within 1 minute, tap below to
                resend it
                <br />
                <Link href="#">
                  <a>Resend</a>
                </Link>
              </div>
            </CardComponent>
          </ContainerComponent>
        </CenterContent>
      </div>
    </div>
  );
};

export default ConfirmAaccount;
