import Head from "next/head";
import Layout from "../../components/Layout";
import Link from "next/link";
import { useState, FormEvent } from "react";
import { Auth } from "../../lib/endpoints";

const Login = () => {
  const [authentication_property, setaAthenticationProperty] = useState("");
  const [password, setPassword] = useState("");

  const authenticate = async (e: FormEvent) => {
    e.preventDefault();
    const rs = await new Auth().login(authentication_property, password);
    console.log(rs);
  };
  return (
    <>
      <Layout>
        <Head>
          <link rel="stylesheet" type="text/css" href="/auth.css" />
        </Head>
        <div
          style={{
            textAlign: "center",
          }}
        >
          <div>
            <h3>
              <b>Login</b>
            </h3>
          </div>
          <br />
        </div>{" "}
        <div className="row">
          <form style={{ width: "100%" }}>
            <div className="form-group col-12">
              <label htmlFor="email">Email address / Phone Number</label>
              <input
                type="email"
                className="form-control cinput"
                id="email"
                aria-describedby="emailHelp"
                placeholder="Enter email or Phone number"
                value={authentication_property}
                onChange={(e) => setaAthenticationProperty(e.target.value)}
              />
            </div>
            <div className="form-group col-12">
              <label htmlFor="exampleInputPassword1">Password</label>
              <input
                type="password"
                className="form-control cinput"
                id="password"
                placeholder="Password"
                data-toggle="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className="form-group col-12">
              <button
                type="submit"
                className="btn btn-primary btn-block submitbutton"
                id="submitButton"
              >
                Login
              </button>
            </div>
            <br />
            <div
              style={{
                textAlign: "center",
              }}
            >
              <a>Forgotten Password?</a>
              <br />
              Don't have an account?
              <Link href="/auth/signup" id="signup">
                <a>Sign Up</a>
              </Link>
            </div>
          </form>
        </div>
      </Layout>
    </>
  );
};

export default Login;
