import Link from "next/link";

const Prompt = (props: any) => {
  return (
    <div
      id="myModal"
      className="modal overlay"
      style={{ display: props.show ? "block" : "", zIndex: 9999 }}
    >
      {/* <!-- Modal content --> */}
      <div
        className="modal-content"
        style={{
          width: "400px",
          position: "absolute",
          left: "50%",
          top: "50%",
          marginLeft: "-150px",
          marginTop: "-150px",
          padding: "20px",
          textAlign: "center",
        }}
      >
        {/* <p>{props.title}</p> */}
        <h5> {props.children}</h5>
        <div
          style={{ display: "flex", textAlign: "center", marginLeft: "35%" }}
        >
          {props.success ? (
            <Link href={props.linkTo}>
              <a className="btn btn-primary">{props.linkText}</a>
            </Link>
          ) : (
            <a
              href="#"
              onClick={() => {
                props.linkTo;
              }}
            >
              {props.linkText}
            </a>
          )}
        </div>
      </div>
    </div>
  );
};

export default Prompt;
