import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

// Hooks
import React, { useState } from "react";

// Next stuff
import Link from "next/link";

const Signup = () => {
  const [validated, setValidated] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [userType, setUserType] = useState("");
  const [isSent, setIsSent] = useState(false);

  const handleSubmit = (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
      event.stopPropagation();
    } else {
      setValidated(true);
      const data = new FormData(event.target);
      console.log(data);
    }
  };

  return (
    <div>
      <CardComponent>
        <HeaderComponent />
      </CardComponent>

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h4>
                Market <span>Circle</span>
              </h4>
              <h5>Make the most of your business</h5>
              <h6>
                Already on market circle?
                <Link href="/marketplace/login">
                  <a>Login</a>
                </Link>
              </h6>
            </header>
            <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email/Phone Number</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Please enter a valid email or phone number."
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                  Please enter email/phone number.
                </Form.Control.Feedback>
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password must be atleast 8 characters long."
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                  Please enter a password.
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password must be atleast 8 characters long."
                  required
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                />
                <Form.Control.Feedback type="invalid">
                  Please confirm password.
                </Form.Control.Feedback>
              </Form.Group>

              <div key="inline-radio" className="mb-3 text-center">
                <Form.Check
                  inline
                  label="Organization"
                  type="radio"
                  name="radioButton"
                  id="inline-radio-1"
                  defaultChecked
                  value={userType}
                  onChange={(e) => setUserType(e.target.value)}
                />
                <Form.Check
                  inline
                  label="Individual"
                  type="radio"
                  name="radioButton"
                  id="inline-radio-2"
                  value={userType}
                  onChange={(e) => setUserType(e.target.value)}
                />
              </div>
              <Button variant="primary" type="submit" block size="lg">
                Signup
              </Button>
            </Form>
            {/* Footer Section */}
            <footer className="text-center">
              <h6>By clicking sign up, you agree to the Market Circle</h6>
              <h6>
                <strong>
                  User Agreement, Privacy Policy and Cookie Policy
                </strong>
              </h6>
            </footer>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default Signup;
