import React, { useState } from "react";
import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Prompt from "../../components/Prompt";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";

// Next stuff
import Link from "next/link";

const Login = () => {
  const [show, setShow] = useState(false);
  const [isRegistered, setIsRegistered] = useState(null);
  const [isInputValid, setIsInputValid] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(null);
  const [promptBody, setPromptBody] = useState("");
  const [linkTo, setLinkTo] = useState(null);
  const [linkText, setLinkText] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading,setLoading] = useState(false)

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = async (event) => {
    event.preventDefault();
    const result = await axios.post("http://51.116.114.155:8080/auth/token", {
      authentication_property: "james@frontend.com",
      password: "passw0rd1",
    });
    console.log("RESULT:", result);
    // set dummy state to demo prompts and login
    // setIsInputValid(false);
    // setIsRegistered(false);
    // // setIsLoggedIn(true)
    // if (isInputValid === false) {
    //   setPromptBody("Please check your credentials and try again.");
    //   handleShow();
    // } else if (isRegistered === false) {
    //   setPromptBody("Please Sign up for an account with us.");
    //   setLinkTo("signup");
    //   setLinkText("Signup");
    //   handleShow();
    // } else if (isLoggedIn) {
    //   // Landing page access goes here
    // }
  };

  return (
    <div>
      <Prompt
        linkTo={linkTo}
        linkText={linkText}
        // body={promptBody}
        show={show}
        handleClose={handleClose}
      >
        <p>{promptBody}</p>
      </Prompt>
      <CardComponent>
        <HeaderComponent />
      </CardComponent>

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h1>Login</h1>
            </header>
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Email</label>
                <input
                  type="email"
                  className="form-control"
                  id="formBasicEmail"
                  placeholder="Please enter a valid email"
                  onChange={(e) => setEmail(e.target.value)}
                />

                <small id="emailHelp" className="form-text text-muted">
                  {/* {emailPhoneValidity} */}
                </small>
              </div>

              <div className="form-group">
                <label>Password</label>
                <input
                  type="password"
                  className="form-control"
                  id="formBasicPassword"
                  placeholder="Password must be atleast 8 characters long."
                  onChange={(e) => setPassword(e.target.value)}
                />
              </div>

              <button
                className="btn btn-primary btn-lg btn-block"
                type="submit"
              >
                
                Login
              </button>
            </Form>
            {/* Footer Section */}
            <footer className="text-center">
              <h6>
                <Link href="/auth/forgottenpassword">
                  <a>Forgoten Password?</a>
                </Link>
              </h6>
              <h6>
                Don't have an account?{" "}
                <Link href="/auth/signup">
                  <a>Signup</a>
                </Link>
              </h6>
            </footer>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default Login;
