import Layout from "../../components/Layout";
import Head from "next/head";
import Button from "react-bootstrap/Button";
import Link from "next/link";
import { useState, createRef } from "react";

const ConfirmAccount = () => {
  const [code, setCode] = useState("");
  const myRef = createRef;

  const autotab = (current, to) => {
    if (
      current.getAttribute &&
      current.value.length == current.getAttribute("maxlength")
    ) {
      to.focus();
    }
  };

  return (
    <>
      <Layout>
        <Head>
          <link rel="stylesheet" type="text/css" href="/auth.css" />
        </Head>
        <div className="wrapper">
          <div className="row">
            <div
              className="col-12"
              style={{
                textAlign: "center",
                alignContent: "center",
                alignItems: "center",
              }}
            >
              <form name="confirmation_code" style={{ width: "100%" }}>
                <div>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    name="number_1"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    name="number_2"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    name="number_3"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                  <input
                    type="text"
                    maxLength={1}
                    size={1}
                    min="0"
                    max="9"
                    name="number_4"
                    pattern="[0-9]{1}"
                    className="verificationbox"
                  ></input>
                </div>
              </form>
            </div>
            <Button
              variant="primary"
              type="submit"
              block
              size="lg"
              className="signup-btn"
            >
              Continue
            </Button>
            <br />
            <div className="text-center">
              If you don't receive the code within 1 minute, tap below to resend
              it
              <br />
              <Link href="#">
                <a>Resend</a>
              </Link>
            </div>
          </div>
        </div>
      </Layout>
    </>
  );
};

export default ConfirmAccount;
