import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "../../public/style.css";
import React, { useState } from "react";

const ReactCodeInput = dynamic(import("react-code-input"));

import ReactInputVerificationCode from "react-input-verification-code";
// import "react-input-verification-code/dist/index.css";

// Next stuff
import Link from "next/link";
import dynamic from "next/dynamic";

const ConfirmAaccount = () => {
  const autotab = (current, to) => {
    if (
      current.getAttribute &&
      current.value.length == current.getAttribute("maxlength")
    ) {
      to.focus();
    }
  };
  const [code, setCode] = useState("");
  console.log("code:", code);
  const myRef = React.createRef;
  return (
    <div>
      <HeaderComponent />
      <div className="wrapper">
        <CenterContent>
          <ContainerComponent>
            <CardComponent width="27em">
              <header className="text-center">
                <h2>Enter code the verfication </h2>
              </header>
              <div className="text-center">
                {/* <ReactCodeInput
                  type="text"
                  fields={4}
                  onChange={() => setCode(value)}
                  inputMode="numeric"
                  name="code"
                /> */}

                <ReactInputVerificationCode
                  length={4}
                  onChange={(e) => setCode(e)}
                />
              </div>

              <button
                id="login_button"
                className="btn btn-primary btn-lg btn-block"
                type="button"
              >
                Continue
              </button>
              <br />
              <div className="text-center">
                If you don't receive the code within 1 minute, tap below to
                resend it
                <br />
                <Link href="#">
                  <a>Resend</a>
                </Link>
              </div>
            </CardComponent>
          </ContainerComponent>
        </CenterContent>
      </div>
    </div>
  );
};

export default ConfirmAaccount;
