import React, { useState } from "react";
import HeaderComponent from "../../components/HeaderComponent";
import CenterContent from "../../components/CenterContent";
import CardComponent from "../../components/CardComponent";
import ContainerComponent from "../../components/ContainerComponent";
import Prompt from "../../components/Prompt";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

// Next stuff
import Link from "next/link";

const Login = () => {
  const [show, setShow] = useState(false);
  const [isRegistered, setIsRegistered] = useState(null);
  const [isInputValid, setIsInputValid] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(null);
  const [promptBody, setPromptBody] = useState("");
  const [linkTo, setLinkTo] = useState(null);
  const [linkText, setLinkText] = useState(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleSubmit = (event) => {
    event.preventDefault();
    // set dummy state to demo prompts and login
    // setIsInputValid(false);
    setIsRegistered(false);
    // setIsLoggedIn(true)
    if (isInputValid === false) {
      setPromptBody("Please check your credentials and try again.");
      handleShow();
    } else if (isRegistered) {
      setPromptBody("Please Sign up for an account with us.");
      setLinkTo("signup");
      setLinkText("Signup");
      handleShow();
    } else if (isLoggedIn) {
      // Landing page access goes here
    }
  };

  return (
    <div>
      <Prompt
        linkTo={linkTo}
        linkText={linkText}
        body={promptBody}
        show={show}
        handleClose={handleClose}
      />
      <CardComponent>
        <HeaderComponent />
      </CardComponent>

      <CenterContent>
        <ContainerComponent>
          <CardComponent title="Signup" footer="User Agreement" width="30rem">
            <header className="text-center">
              <h1>Login</h1>
            </header>
            <Form onSubmit={handleSubmit}>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email/Phone Number</Form.Label>
                <Form.Control
                  type="email"
                  placeholder="Please enter a valid email or phone number."
                />
                <Form.Text className="text-muted"></Form.Text>
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  placeholder="Password must be atleast 8 characters long."
                />
              </Form.Group>

              <Button variant="primary" type="submit" block size="lg">
                Login
              </Button>
            </Form>
            {/* Footer Section */}
            <footer className="text-center">
              <h6>
                <Link href="/marketplace/forgottenpassword">
                  <a>Forgoten Password?</a>
                </Link>
              </h6>
              <h6>
                Don't have an account?{" "}
                <Link href="/marketplace/signup">
                  <a>Signup</a>
                </Link>
              </h6>
            </footer>
          </CardComponent>
        </ContainerComponent>
      </CenterContent>
    </div>
  );
};

export default Login;
